<?php

namespace CudiBundle\Repository\Sales\Session;

use Doctrine\ORM\EntityRepository;

/**
 * OpeningHour
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class OpeningHour extends EntityRepository
{
    public function findAllActive()
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('o')
            ->from('CudiBundle\Entity\Sales\Session\OpeningHour', 'o')
            ->where(
                $query->expr()->gte('o.endDate', ':now')
            )
            ->setParameter('now', new \DateTime())
            ->getQuery()
            ->getResult();

       return $resultSet;
    }

    public function findAllOld()
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('o')
            ->from('CudiBundle\Entity\Sales\Session\OpeningHour', 'o')
            ->where(
                $query->expr()->lt('o.endDate', ':now')
            )
            ->setParameter('now', new \DateTime())
            ->getQuery()
            ->getResult();

       return $resultSet;
    }

    public function findCurrent()
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('o')
            ->from('CudiBundle\Entity\Sales\Session\OpeningHour', 'o')
            ->where(
                $query->expr()->andX(
                    $query->expr()->lt('o.startDate', ':now'),
                    $query->expr()->gt('o.endDate', ':now')
                )
            )
            ->setParameter('now', new \DateTime())
            ->setMaxResults(1)
            ->getQuery()
            ->getResult();

        if (isset($resultSet[0]))
            return $resultSet[0];

        return null;
    }
}
